
public class SwitchStringExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String position = "과장";
		
		switch(position) {
			case "부장":
				System.out.println(position+": 700만원");
				break;
				
			case "과장":
				System.out.println(position+": 500만원");
				break;					
				
			default:
				System.out.println(position+": 300만원");
		}
	}
}